<?php

namespace TS\AssetsBundle\Repository;

/**
 * EquipmentRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class EquipmentRepository extends \Doctrine\ORM\EntityRepository
{
    public function getEquipments($id) {
        $qb = $this->createQueryBuilder('p');
        return $qb->where('p.substation = :substation')
                ->setParameter('substation', $id)
                ->getQuery()
                ->getArrayResult();
    }

    public function getQueryEquipments($id) {
        return $this->createQueryBuilder('p')
                    ->where('p.substation = :substation')
                    ->setParameter('substation', $id);
    }
}
